import React from "react";
import { useState } from "react";
import { useContext } from "react";
import trackerContext from "../conetx/trackerContext";

const Form = () => {
  const [inc, setInc] = useState("");
  const [exp, setExp] = useState("");
  const [desc, setDesc] = useState("");
  const { transections } = useContext(trackerContext);
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`submit`);
  };
  return (
    <form onSubmit={handleSubmit}>
      <h4>Add Your Income or Expense</h4>
      <div className="row g-2">
        <div className="col-auto">
          <label htmlFor="income" className="d-block">
            Income
          </label>
          <input
            type="number"
            name="income"
            id="income"
            value={inc}
            onChange={(e) => setInc(e.target.value)}
            disabled={exp}
          />
        </div>
        <div className="col-auto">
          <label htmlFor="expense" className="d-block">
            Expense
          </label>
          <input
            type="number"
            name="expense"
            id="expense"
            value={exp}
            onChange={(e) => setExp(e.target.value)}
            disabled={inc}
          />
        </div>
      </div>
      <div>
        <label htmlFor="description" className="d-block">
          Description
        </label>
        <input
          type="text"
          name="description"
          id="description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
        />
      </div>
      <input type="submit" className="btn btn-primary mt-1" />
    </form>
  );
};

export default Form;
