import * as mode from "./actions";
export default (state, action) => {
  switch (action.type) {
    case mode.ADD_TRANSECTION:
      return {
        ...state,
        transections: [action.payload, ...state.transections],
          income: [...state.income, state.transections.map(trans => trans.type.inc && trans.type)]
      };
    default:
      return state;
  }
};
