import React from "react";
import { useState } from "react";
import { useContext } from "react";
import trackerContext from "../conetx/trackerContext";
import { v4 as uuidv4 } from "uuid";
import { ToastContainer, toast } from "react-toastify";

const Form = () => {
  const [inc, setInc] = useState("");
  const [exp, setExp] = useState("");
  const [desc, setDesc] = useState("");
  const { addTransection } = useContext(trackerContext);
  const handleSubmit = (e) => {
    e.preventDefault();
    const id = uuidv4();
    if (inc) {
      addTransection({ id, inc, desc });
      setInc("");
      setDesc("");
    } else if (exp) {
      addTransection({ id, exp, desc });
      setExp("");
      setDesc("");
    }
    toast("ðŸ¦„ Wow so easy!", {
      position: "top-right",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };
  return (
    <form onSubmit={handleSubmit}>
      <h4>Add Your Income or Expense</h4>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      {/* Same as */}
      <ToastContainer />
      <div className="row g-2">
        <div className="col-auto">
          <label htmlFor="income" className="d-block">
            Income
          </label>
          <input
            type="number"
            name="income"
            id="income"
            value={inc}
            onChange={(e) => setInc(e.target.value)}
            disabled={exp}
          />
        </div>
        <div className="col-auto">
          <label htmlFor="expense" className="d-block">
            Expense
          </label>
          <input
            type="number"
            name="expense"
            id="expense"
            value={exp}
            onChange={(e) => setExp(e.target.value)}
            disabled={inc}
          />
        </div>
      </div>
      <div>
        <label htmlFor="description" className="d-block">
          Description
        </label>
        <input
          type="text"
          name="description"
          id="description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
        />
      </div>
      <input type="submit" className="btn btn-primary mt-1" />
    </form>
  );
};

export default Form;
